import { useState } from "react";
import { getDocument } from "../api";

const FileList = ({ files }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [showParsedText, setShowParsedText] = useState(false);
  const [showPreview, setShowPreview] = useState(false);

  const handleFetchAndPreview = async (fileId) => {
    try {
      const file = await getDocument(fileId);
      setSelectedFile(file);
      setShowPreview(true);
    } catch (error) {
      console.error("Failed to fetch document:", error);
    }
  };

  const handleShowParsedText = (file) => {
    setSelectedFile(file);
    setShowParsedText(true);
  };

  return (
    <div className="mt-6">
      <h3 className="text-lg font-bold mb-4">Uploaded Files</h3>
      <ul className="space-y-4">
        {files.map((file) => (
          <li
            key={file.file_id}
            className="p-4 border rounded-lg shadow-md bg-white flex justify-between items-center"
          >
            <span>{file.filename}</span>
            <div className="flex space-x-4">
              <button
                onClick={() => handleFetchAndPreview(file.file_id)}
                className="text-blue-500 underline"
              >
                Fetch & Preview
              </button>
              <button
                onClick={() => handleShowParsedText(file)}
                className="text-green-500 underline"
              >
                Show Parsed Text
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* PDF Preview Modal */}
      {showPreview && selectedFile && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg max-w-3xl w-full">
            <h3 className="text-lg font-bold mb-4">PDF Preview: {selectedFile.filename}</h3>
            <iframe
              src={`http://127.0.0.1:8000/uploads/${selectedFile.filepath}`} // Ensure this URL is correct
              title="PDF Preview"
              className="w-full h-96 border rounded"
            ></iframe>
            <button
              className="bg-red-500 text-white px-4 py-2 mt-4 rounded"
              onClick={() => setShowPreview(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Parsed Text Modal */}
      {showParsedText && selectedFile && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg max-w-lg w-full">
            <h3 className="text-lg font-bold mb-4">Parsed Text</h3>
            <div className="overflow-y-auto max-h-64 border p-4 rounded bg-gray-50">
              {selectedFile.parsed_text || "No parsed text available"}
            </div>
            <button
              className="bg-red-500 text-white px-4 py-2 mt-4 rounded"
              onClick={() => setShowParsedText(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FileList;
