import React, { useState } from "react";

const FileList = ({ files }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [showParsedText, setShowParsedText] = useState(false);

  const handlePreview = (file) => {
    setSelectedFile(file); // Set the file for preview
  };

  const handleShowParsedText = (file) => {
    setSelectedFile(file); // Set the file for parsed text
    setShowParsedText(true); // Open the modal
  };

  const handleDownloadParsedText = (file) => {
    const blob = new Blob([file.parsed_text || "No parsed text available"], {
      type: "text/plain",
    });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${file.filename}_parsed.txt`;
    link.click();
  };

  return (
    <div className="mt-6">
      <h2 className="text-lg font-bold text-gray-700 mb-4">Uploaded Files</h2>
      <ul className="space-y-4">
        {files.map((file) => (
          <li
            key={file.id}
            className="bg-white shadow rounded-lg p-4 flex justify-between items-center"
          >
            <div>
              <strong className="text-gray-800">{file.filename}</strong>
            </div>
            <div className="flex space-x-2">
              <button
                className="bg-blue-500 text-white px-3 py-1 rounded"
                onClick={() => handlePreview(file)}
              >
                Preview PDF
              </button>
              <button
                className="bg-green-500 text-white px-3 py-1 rounded"
                onClick={() => handleShowParsedText(file)}
              >
                Show Parsed Text
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* PDF Preview */}
      {selectedFile && (
        <div className="mt-6">
          <h3 className="text-lg font-bold mb-2">PDF Preview: {selectedFile.filename}</h3>
          <iframe
            src={`http://127.0.0.1:8000/uploads/${selectedFile.filepath}`} // Use only the filename
            title="PDF Preview"
            className="w-full h-96 border rounded"
          ></iframe>
        </div>
      )}

      {/* Parsed Text Modal */}
      {showParsedText && selectedFile && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-lg max-w-lg w-full">
            <h3 className="text-lg font-bold mb-4">Parsed Text</h3>
            <div className="overflow-y-auto max-h-64 border p-4 rounded bg-gray-50">
              {selectedFile.parsed_text || "No parsed text available"}
            </div>
            <button
              className="bg-red-500 text-white px-4 py-2 mt-4 rounded"
              onClick={() => setShowParsedText(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FileList;
